import React, { useRef, useState } from 'react';
import Image from './Assets/pic.jpeg'
import './Card.css'
import Vid from './Assets/'

const Card = () => {
    const videoRef = useRef(null);
    const cardRef = useRef(null);  // Reference to the card for focus handling
    const [isFocused, setIsFocused] = useState(false);  // State to track if the card is focused
    const [isHovered, setIsHovered] = useState(false);  // State to track if the card is hovered

    // Event handler to start video playback when the mouse enters
    const handleMouseEnter = () => {
        setIsHovered(true);
        if (!isFocused && videoRef.current) {
            videoRef.current.play();
        }
    };

    // Event handler to pause video playback when the mouse leaves, but not if it's clicked (focused)
    const handleMouseLeave = () => {
        setIsHovered(false);
        if (!isFocused && videoRef.current) {
            videoRef.current.pause();
            videoRef.current.currentTime = 0;  // Optionally, reset to start
        }
    };

    // Event handler to start video playback when the card is clicked and gains focus
    const handleCardClick = () => {
        if (videoRef.current) {
            videoRef.current.play();
            setIsFocused(true);  // Set focus state to true
        }
    };

    // Event handler to pause video playback when the card loses focus
    const handleCardBlur = () => {
        if (videoRef.current) {
            videoRef.current.pause();
            videoRef.current.currentTime = 0;  // Optionally, reset to start
            setIsFocused(false);  // Set focus state to false
        }
    };

    return (
        <div
            className="card"
            ref={cardRef}
            tabIndex={0}  // Make the div focusable
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            onClick={handleCardClick}
            onBlur={handleCardBlur}  // Handles when the card loses focus
        >
            {/* Show image when video is not playing (not hovered or focused) */}
            {!isHovered && !isFocused && (
                <img
                    src={Image}
                    alt="Card Thumbnail"
                    className="card-image"
                    width="300"
                    height="200"
                />
            )}

            {/* Video element that appears when hovered or focused */}
            {(isHovered || isFocused) && (
                <video
                    ref={videoRef}
                    width="300"
                    height="200"
                    loop
                    muted
                    className="card-video"
                >
                    <source src="" type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            )}

            <div className="card-content">
                <h3>Pipou</h3>
                <p>Only a few people know Pipou </p>
            </div>
        </div>
    );
};

export default Card;
