import React from "react";
import "./UserCard.css"; // Import the CSS file for styling

const ProfileCard = ({ fullName, role, imageUrl }) => {
  return (
    <div className="card">
      {/* Circular Div with background image as the avatar */}
      <div
        className="avatar"
        style={{ backgroundImage: `url(${imageUrl})` }}
      ></div>

      {/* Content (Full Name and Role) */}
      <div className="content">
        <h2 className="name" pla>{fullName}</h2>
        <p className="role">{role}</p>
      </div>
    </div>
  );
};

export default ProfileCard;
